mapper.update:
update tb_driver
set archive = 1
where id = #{driverId}

mapper.select:
select count(id) as ct
from tb_driver td


        and td.open_id = #{openId}


        and td.id = #{id}

select cast(id as char) as id
from tb_driver td
where td.open_id = #{openId}

select td.name, td.sex
from tb_driver td
where td.id = #{driverId}

SELECT CAST(id AS CHAR) as id,
IFNULL(td.name,"") ,
IFNULL(td.sex,""),
IFNULL(td.pid,""),
IFNULL(td.tel,"") ,
IFNULL(td.contact_name,"") AS contactName,
IFNULL(td.contact_tel,"") AS contactTel,
IFNULL(td.real_auth,"") AS realAuth,
td.status,
DATE_FORMAT(create_time, '%Y-%m-%d') AS createTime
FROM tb_driver td


        AND td.name = #{name}


        AND tel = #{tel}


        AND pid = #{pid}


        AND sex = #{sex}


        AND td.real_auth = #{realAuth}


        AND td.status = #{status}


LIMIT #{start}, #{length}

SELECT COUNT(*)
FROM tb_driver td


        AND td.name = #{name}


        AND tel = #{tel}


        AND pid = #{pid}


        AND sex = #{sex}


        AND td.real_auth = #{realAuth}


        AND td.status = #{status}
