mapper.update:
update tb_driver
set archive = 1
where id = #{driverId}

mapper.select:
select count(id) as ct
from tb_driver td


        and td.open_id = #{openId}


        and td.id = #{id}

select cast(id as char) as id
from tb_driver td
where td.open_id = #{openId}

select td.name, td.sex
from tb_driver td
where td.id = #{driverId}
